  marks: [
    Plot.areaY(dat_d7, { // edits here
      x: "year",
      y: "value",
      fill: "value",
      color: "#00000000",
      z: "null",
      fx: (d) =&gt; fx(d.country_name),
      fy: (d) =&gt; fy(d.country_name),
      strokeWidth: 0.05,
      curve: "catmull-rom",
      tip: true
    }),
    Plot.text(keys, { fx, fy, frameAnchor: "top-left", dx: 6, dy: 6 }),
    Plot.frame({ strokeWidth: 0.5 })
  ]
})

