d7 = require('d3@7') 
dat_d7 = d7.csv('govt_spend_primary.csv', function(d){
  return {
    series_name: d.series_name,
    country_name: d.country_name,
    country_code: d.country_code,
    year: +d.year,
    value: +d.value
  }
})
